create table PROPTYPES (id int identity(1,1) primary key,"name" nvarchar(200) not null,prefix nvarchar(20),postfix nvarchar(20),"desc" nvarchar(500),url nvarchar(200));create table PROPS ( id int identity(1,1) primary key, id_type int not null, "value" nvarchar(200), constraint fk_propval_proptype foreign key (id_type) references proptypes(id));create table CATS( id int identity(1,1) primary key, "name" nvarchar(200) not null, "desc" nvarchar(1000), full_desc nvarchar (4000), "url" nvarchar, parent_id int, parent_name nvarchar(200));alter table cats add   constraint fk_parent_cat foreign key (parent_id) references cats(id);create table cat_proptypes ( cat_id int,  constraint fk_cat_ptcats foreign key(cat_id) references Cats(id), proptype_id int, constraint fk_pt_ptcats foreign key (proptype_id) references proptypes(id), constraint pk primary key (cat_id,proptype_id) );create table brands( id int identity(1,1) primary key, "name" varchar(200) not null, full_desc varchar(4000), "desc" varchar(100), url varchar(200));create table PRODUCTS ( id int identity(1,1) primary key, "name" nvarchar(200) not null, "desc" nvarchar(2000), full_desc nvarchar(max), "url" nvarchar(200), Cat_id int, price decimal, constraint fk_product_cat foreign key(Cat_id) references Cats(id), cat_name nvarchar(200), brand_id int, constraint fk_product_brand foreign key (brand_id) references brands(id), brand_name nvarchar(200));alter table PRODUCTS add Images nvarchar(500);create table Prod_Props (product_id int not null,prop_id int not null,constraint pk_ primary key (product_id,prop_id),constraint fk_product foreign key (product_id) references Products(id),constraint fk_prop foreign key (prop_id) references props(id));insert into brands (name)values('Экопром'),('Джилекс');